import RegisterComponent from "../../componentRegister";
import template from './form-button-template';

RegisterComponent('form-button', {
    script: (props) => {
        const btnType = props.type || 'default';
        const enable = props.enable || true;
        const visible = props.visible || true;
        const style = "btn btn-" + btnType;

        return {
            click: props.click,
            text: props.text,
            translationKey: props.translationKey,
            tooltip: props.tooltip,
            enable,
            visible,
            style
        };
    },
    template: template
});