ko.components.register('action-button', {
    viewModel: {
        createViewModel: function(params) {
            return {
                mainIcon: params.mainIcon || 'fa-ellipsis-h',
                visible: params.visible || true,
                enable: params.enable || true,
                actions: params.actions
            };
        }
    },
    template:
        `<span class="dropdown">
            <button type="button" class="btn btn-primary dropdown-toggle" data-toggle="dropdown" data-bind="enable: enable, visible: visible">\
                <span><i class="fa" data-bind="css: mainIcon"></i></span>
            </button>
            <ul class="dropdown-menu pull-right" data-bind="foreach: actions">\
                <li><a href="#" data-bind="click: action, text: title, visible: isVisible"></a></li>\
            </ul>
        </span>`
});
