ko.bindingHandlers.htmlTooltip = {
    init: (element, valueAccesor) => {
        $(element).tooltip({
            html: true,
            title: valueAccesor()
        }).addClass('html-tooltip');
    },
    update: (element, valueAccesor) => {
        $(element).attr('title', valueAccesor()).tooltip('fixTitle');
    }
};