export default function TranslationKeyBinding(ko, translator) {
    ko.bindingHandlers.translationKey = {
        update: (element, valueAccessor) => {
            translate(element, valueAccessor, translator);
        }
    };

    function translate(element, valueAccessor, translator) {
        const translationKey = ko.unwrap(valueAccessor());
        const translation = translationKey != undefined ? translator.translate(translationKey) : '';
        ko.utils.setTextContent(element, translation);
    }
}