import ErrorMessagesExtractor from "./dialogs/errorMessagesExtractor";
import ColumnsDefinitionBuilder from "./components/search-results-table/columnDefinitionsBuilder";
import ComponentRegister from "./componentRegister";
import { DependencyProviderFactory } from "./dependencyProviderFactory";
import { DialogPublisher } from "./DialogPublisher";
import DialogViewModel from "./Dialogs";
import History from "./History";
import RestClient from "./restClient";
import Tabs from "./components/tabs/tabs";
import TranslationService from "./TranslationService";
import UserRightsService from "./UserRightsService";
import ValidationService from "./validationService";
import Validator from "./validator";
import DefineRemoteSelect2 from "./knockoutComponents/knockout-remoteSelect2";
import TranslationKeyBinding from "./knockoutComponents/knockout-translationKey";

export function exportApi() {
    return {
        ErrorMessagesExtractor,
        ColumnsDefinitionBuilder,
        ComponentRegister,
        DependencyProviderFactory,
        DialogPublisher,
        DialogViewModel,
        History,
        Tabs,
        RestClient,
        TranslationService,
        UserRightsService,
        ValidationService,
        Validator,
        DefineRemoteSelect2,
        TranslationKeyBinding
    };
}

var aidaCore = require('aida-core').exportApi();
window.aidaCore = aidaCore;