import template from './color-selector-template';

aidaCore.ComponentRegister('color-selector', {
    script: (props, dependencyProvider) => {
        const translator = dependencyProvider.getTranslator();
        const enableEvents = props.enableEvents || ko.observable(true);
        const whenColorSelected = props.whenColorSelected || (() => {});

        function getLabels() {
            return {
                label: translator.translate('SharedDomain.Vehicle.Color'),
                placeholder: translator.translate('SharedDomain.SelectValue')
            };
        }

        return {
            labels: getLabels(),
            options: props.options,
            selected: props.selected,
            enableEvents,
            whenColorSelected
        };
    },
    template: template
});