export default function VehicleSummary() {
    const model = ko.observable();
    const color = ko.observable();
    const interior = ko.observable();
    const equipment = ko.observableArray([]);

    function update(newModel, newColor, newInterior, newEquipment) {
        model(newModel);
        color(newColor);
        interior(newInterior);
        equipment.removeAll();
        ko.utils.arrayPushAll(equipment, newEquipment);
    }

    function isValid() {
        return model() && color() && interior();
    }

    return {
        update,
        isValid,
        model,
        color,
        interior,
        equipment
    };
}