export default function SelectableEquipment(selectableItem, whenSelected, whenUnselected) {
    const isSelected = selectableItem.selected;

    function selectOrUnselect() {
        if (isSelected()) {
            whenUnselected(self);
        } else {
            whenSelected(self);
        }
    }

    function unselect() {
        isSelected(false);
    }

    let self = {
        isSelected,
        selectOrUnselect,
        unselect,
        code: selectableItem.code,
        descriptionWithoutCode: selectableItem.descriptionWithoutCode,
        category: selectableItem.category,
        contains: selectableItem.contains,
        isPackage: selectableItem.isPackage,
        family: selectableItem.family
    };
    return self;
}