import template from './interior-selector-template';

aidaCore.ComponentRegister('interior-selector', {
    script: (props, dependencyProvider) => {
        const translator = dependencyProvider.getTranslator();
        const enableEvents = props.enableEvents || ko.observable(true);
        const whenInteriorSelected = props.whenInteriorSelected || (() => {});

        function getLabels() {
            return {
                label: translator.translate('SharedDomain.Vehicle.Interior'),
                placeholder: translator.translate('SharedDomain.SelectValue')
            };
        }

        return {
            labels: getLabels(),
            options: props.options,
            selected: props.selected,
            enableEvents,
            whenInteriorSelected
        };
    },
    template: template
});