export default function VehicleImageIdentifier(modelId, colorCode, interiorCode) {
    const emptyValue = null;

    function reset() {
        if (modelId) modelId = emptyValue;
        if (colorCode) colorCode = emptyValue;
        if (interiorCode) interiorCode = emptyValue;
    }

    return {
        reset,
        modelId, colorCode, interiorCode
    };
}