import RegisterComponent from "../../componentRegister";
import template from "./search-box-template";

RegisterComponent('search-box', {
    script: {
        create: (props, dependencyProvider) => {
            const translator = dependencyProvider.getTranslator();

            function clear() {
                props.searchTerm('');
                if (props.onClear) props.onClear();
            }

            function getLabels() {
                return {
                    search: translator.translate("SharedDomain.SearchButton.Text")
                };
            }

            return {
                labels: getLabels(),
                searchTerm: props.searchTerm,
                search: props.onSearch,
                clear: clear
            };
        }
    },
    template: template
});