
export default function ColumnsDefinitionBuilder() {
    let definitions = [];

    function addColumn(name, label, type = 'text', align = 'left', hideWhen = 'never') {
        definitions.push({ name: name, label: label, type: type, align: align, hideWhen: hideWhen });
    }

    function build() {
        return definitions;
    }

    return {
        build,
        addColumn
    };
}