import ComponentRegister from '../../../componentRegister';
import template from "./tab-content-template";

ComponentRegister('tab-content', {
    script: (props) => {
        return TabContent(props.id, props.tabs);
    },
    template: template
});

export default function TabContent(id, tabs) {
    function isActive() {
        return id == tabs.currentTab();
    }

    return {
        isActive
    };
}