ko.components.register('columncheckbox', {
    viewModel: {
        createViewModel: function(params) {
            const select = params.select;
            const unselect = params.unselect;
            const checked = params.checked;
            
            checked.subscribe(()=> {
                if (checked()) select();
                if (!checked()) unselect();
            });
            
            return { checked };
        } 
    },
    
    template:
    '<input id="columnChecker" type="checkbox" data-bind="checked: checked">'
});