ko.bindingHandlers.shortDateTimeText = {
    init: () => {
        return { 'controlsDescendantBindings': true };
    },
    update: (element, valueAccessor) => {
        const valueAsISO8061 = ko.unwrap(valueAccessor());
        const dateAsText = valueAsISO8061 ? moment(valueAsISO8061).format('L') : '';       
        const timeAsText = valueAsISO8061 ? moment(valueAsISO8061).format('LTS') : '';
        ko.utils.setTextContent(element, `${dateAsText} ${timeAsText}`);
    }
};